import XCTest
@testable import ImageFeed

class ImageBodyResponseParserTests: XCTestCase {
    
    override func setUp() {
        super.setUp()
        // Put setup code here. This method is called before the invocation of each test method in the class.
    }
    
    override func tearDown() {
        // Put teardown code here. This method is called after the invocation of each test method in the class.
        super.tearDown()
    }
    
    func testEmptyData() {
        let parser = ImageBodyResponseParser(data: nil, response: nil, error: nil)
        parser.parse()
        switch parser.error! {
        case NetworkError.emptyBody:
            ()
        default:
            XCTAssertFalse(true, "Empty body should be emitted")
        }
    }
    
    func testInvalidData() {
        let data = Data(bytes: [1, 2, 3])
        let parser = ImageBodyResponseParser(data: data, response: nil, error: nil)
        parser.parse()
        switch parser.error! {
        case NetworkError.invalidBody:
            ()
        default:
            XCTAssertFalse(true, "Invalid body should be emitted")
        }
    }
    
    func testValidData() {
        let data = Data(base64Encoded: "")!
        let parser = ImageBodyResponseParser(data: data, response: nil, error: nil)
        parser.parse()
        XCTAssertNotNil(parser.result)
    }
    
}
